import { makeStyles } from '@material-ui/core/styles';

export default makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      position: 'absolute',
    right: "150px",
    width: '200px',
    height: '120px',

    },
  },
  paper: {
    padding: theme.spacing(2),

  },
  form: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    position: 'absolute',
    right: "150px",
    width: '200px',
    height: '120px',

  },
  fileInput: {
    width: '97%',
    margin: '10px 0',
  },
  buttonSubmit: {
    marginBottom: 10,
  },
}));
